INSERT INTO `view` VALUES (49322, '丽江', '丽江', '大雨', '丽江市区中心海拔高度为2418米，与同为第二批国家历史文化名城的四川阆中、山西平遥、安徽歙县并称为“保存最为完好的四大古城”。', '/asserts/img/viewImg/small3bb6c645c7922a9027dac1e08fd3777e1567645553.jpg');
INSERT INTO `view` VALUES (50527, '乌镇', '嘉兴', '晴', '中国美景10大排名第四是有“鱼米之乡”之称的乌镇，是国内典型的江南水乡小镇，镇中保存着原有的晚清和民国时期水乡古镇的格局和风貌，很适合坐着乌篷船穿行在各种的古朴建筑之中，感受难得的闲适。\r\n\r\n　　门票：东栅：100.00元 西栅：120.00元 联票(含东栅、西栅)：150.00元\r\n\r\n　　开放时间：夏季(5月1日-10月8日) 东栅：7:20-18:00;西栅：9:00-22:30 冬季(10月9日-4月30日) 东栅：7:20-17:30;西栅：9:00-22:00\r\n\r\n　　地址：浙江省嘉兴桐乡石佛南路18号', '/asserts/img/viewImg/smallb697d5151487ef327751127c10d7d6b81554126188.jpg');
INSERT INTO `view` VALUES (52742, '九寨沟', '四川', '多云', '九寨沟是一个集合了神秘、浪漫的地方，也是国内一生必去的10个地方，这里的湖水晶莹平静，五花海让人无不惊叹，还有参天的古树和野生动物，走在九寨沟中就仿佛步入了人间仙境。\r\n\r\n　　门票：旺季(04月01日~11月15日)：220.00元 淡季(11月16日~03月31日)：80.00元\r\n\r\n　　开放时间：旺季(04月01日~11月15日)：06:30~18:00 淡季(11月16日~03月31日)：06:30~17:00\r\n\r\n　　地址：四川省阿坝藏族羌族自治州九寨沟县', '/asserts/img/viewImg/dd9fdc8a9bce2e196281051bc1891360.jpg');
INSERT INTO `view` VALUES (55114, '.呼伦贝尔大草原', '呼伦巴尔', '小雨', '呼伦贝尔大草原一直都是中外闻名的旅游胜地，这里的环境生态保存完好，被称为“牧草王国”，草原干净纯粹、一望无际，大片的绿色净土使到此游玩的游客心旷神怡，感受天之大，世界十大，可以使人忘却自己的烦恼。\r\n\r\n　　门票：20.00元\r\n\r\n　　开放时间：全天开放\r\n\r\n　　地址：内蒙古呼伦巴尔市陈巴尔虎旗', '/asserts/img/viewImg/small88a0c2293416705dd1df2a618169d9ba1572793475.jpg');
INSERT INTO `view` VALUES (55992, '.呼伦贝尔大草原', '呼伦巴尔', '中雨', '呼伦贝尔大草原一直都是中外闻名的旅游胜地，这里的环境生态保存完好，被称为“牧草王国”，草原干净纯粹、一望无际，大片的绿色净土使到此游玩的游客心旷神怡，感受天之大，世界十大，可以使人忘却自己的烦恼。\r\n\r\n　　门票：20.00元\r\n\r\n　　开放时间：全天开放\r\n\r\n　　地址：内蒙古呼伦巴尔市陈巴尔虎旗', '/asserts/img/viewImg/small88a0c2293416705dd1df2a618169d9ba1572793475.jpg');
INSERT INTO `view` VALUES (57097, '银屿岛', '南海', '多云', '早上出海钓鱼拖钓或者拍照，下午浮潜抓鱼抓龙虾抓海胆，傍晚看夕阳吹风聊天，岛上居民也是超级热情，随便唠几句就能免费吃上最新鲜的海鲜。这片海和岛上的人，都让我一发不可收拾的爱上西沙！重点是人少人少人少，整个人都安静下来了，真的是世外桃源', '/asserts/img/viewImg/c6d42913c0440bdb5ffeec9c90236d66.jpg');
INSERT INTO `view` VALUES (59411, '秦始皇兵马俑', '西安', '小雨', '秦始皇兵马俑被称为世界第一大奇迹，规模巨大，气质恢宏，备受中外游客的关注，到这里游玩的时候可以找一个讲解员，一边游览一边了解历史背景和背后的故事能给人留下更深的记忆。\r\n\r\n　　门票：旺季(3月1日~11月31日)：150.00元 淡季(12月1日~2月28日)：120.00元\r\n\r\n　　开放时间：3月16日~11月14日： 8:30~17:30 11月15日~3月15日： 8:30~17:00\r\n\r\n　　地址：西安市临潼区临蓝路', '/asserts/img/viewImg/small3bb6c645c7922a9027dac1e08fd3777e1567645553.jpg');
INSERT INTO `view` VALUES (59761, '桂林漓江', '桂林', '多云', '桂林漓江风景区中有着“桂林山水甲天下”的精华，漓江本身仿佛一条青色的缎带蜿蜒在山谷之中，坐船游江是最好的感受漓江美的方法，你可以看到两侧奇峰倒影，层峦叠嶂，，被人们称作“百里画廊”。\r\n\r\n　　门票：旺季(4月~11月)：210.00元 淡季(12月~3月)：190.00元\r\n\r\n　　开放时间：旺季(4月~11月)：08:40~22:40 淡季(12月~3月)：08:40~21:40\r\n\r\n　　地址：桂林市东北兴安县猫儿山', '/asserts/img/viewImg/smallb697d5151487ef327751127c10d7d6b81554126188.jpg');
INSERT INTO `view` VALUES (61155, '九寨沟', '四川', '阴', '九寨沟是一个集合了神秘、浪漫的地方，也是国内一生必去的10个地方，这里的湖水晶莹平静，五花海让人无不惊叹，还有参天的古树和野生动物，走在九寨沟中就仿佛步入了人间仙境。\r\n\r\n　　门票：旺季(04月01日~11月15日)：220.00元 淡季(11月16日~03月31日)：80.00元\r\n\r\n　　开放时间：旺季(04月01日~11月15日)：06:30~18:00 淡季(11月16日~03月31日)：06:30~17:00\r\n\r\n　　地址：四川省阿坝藏族羌族自治州九寨沟县', '/asserts/img/viewImg/dd9fdc8a9bce2e196281051bc1891360.jpg');
INSERT INTO `view` VALUES (62769, '乌镇', '嘉兴', '多云', '中国美景10大排名第四是有“鱼米之乡”之称的乌镇，是国内典型的江南水乡小镇，镇中保存着原有的晚清和民国时期水乡古镇的格局和风貌，很适合坐着乌篷船穿行在各种的古朴建筑之中，感受难得的闲适。\r\n\r\n　　门票：东栅：100.00元 西栅：120.00元 联票(含东栅、西栅)：150.00元\r\n\r\n　　开放时间：夏季(5月1日-10月8日) 东栅：7:20-18:00;西栅：9:00-22:30 冬季(10月9日-4月30日) 东栅：7:20-17:30;西栅：9:00-22:00\r\n\r\n　　地址：浙江省嘉兴桐乡石佛南路18号', '/asserts/img/viewImg/smallb697d5151487ef327751127c10d7d6b81554126188.jpg');
INSERT INTO `view` VALUES (69497, '三亚', '三亚', '多云', '三亚市位于海南岛最南端，因三亚河(古名临川水)有三亚东西2河至此会合，成“丫”字形，故取名“三亚”，东邻陵水县，西接乐东县，北毗保亭县，南临南海及三沙市，陆地总面积1919.58平方公里，海域总面积6000平方公里，人口68.5万，是一个黎、苗、回、汉多民族聚居的地区。三亚是中国最南部的滨海旅游城市，是海南省南部的中心城市和交通通信枢纽，是中国东南沿海对外开放黄金海岸线上最南端的对外贸易重要口岸。', '/asserts/img/viewImg/small32f115ed6caaff1bdf4e395c7615333f1592234233.jpg');
INSERT INTO `view` VALUES (73268, '香格里拉', '香格里拉', '阴', '香格里拉的天空号称是全国最美的天空之一，这里的空气十分清新，被称为离天堂最近的地方。浓郁的藏族民族特色也是香格里拉很吸引人的一点，广阔的草坪，神圣的雪山，幽深的峡谷，宁静的湖泊可以让你看到世界之大，忘却一切烦恼。\r\n\r\n　　门票：免费\r\n\r\n　　开放时间：全天\r\n\r\n　　地址：云南省西北部，邻接四川省', '/asserts/img/viewImg/6407071345cbdb5846de1014cc251eab.jpg');
INSERT INTO `view` VALUES (74291, '张家界', '张家界', '多云', '说到张家界，可以说是山川情怀的圣地了，再加上星球上最奇特的喀斯特地貌，千柱石山奇，看完张家界不用再看其他地方的山了。我一共去过三次。\r\n\r\n第一次：跟团走费用比较贵，很多的时间用来逛商场，上了团还有各种乱七八糟的费用，也是玩的最不开心的一次。\r\n\r\n第二次：自己先攻略那自然是费时间，需要提前一个月就开始在网上找各种资料。需要了解那里的风俗，交通，酒店，门票等等，非一般的累，虽然最后玩起来很自由，但很少有人花那么多时间来查资料，特别麻烦的就是酒店和交通。\r\n\r\n第三次：这次是我在网上面找到了一个有口碑的自由行规划师冉冉，由她来制行程，安排酒店和门票，我们只管玩就是了，这种呢酒店、门票都帮你提前安排了哦，价格还比市面上的低一点点）', '/asserts/img/viewImg/small39234b42ec314a0dc35f39e9eaa9be331548216596.jpg');
INSERT INTO `view` VALUES (75825, '九寨沟', '四川', '晴', '九寨沟是一个集合了神秘、浪漫的地方，也是国内一生必去的10个地方，这里的湖水晶莹平静，五花海让人无不惊叹，还有参天的古树和野生动物，走在九寨沟中就仿佛步入了人间仙境。\r\n\r\n　　门票：旺季(04月01日~11月15日)：220.00元 淡季(11月16日~03月31日)：80.00元\r\n\r\n　　开放时间：旺季(04月01日~11月15日)：06:30~18:00 淡季(11月16日~03月31日)：06:30~17:00\r\n\r\n　　地址：四川省阿坝藏族羌族自治州九寨沟县', '/asserts/img/viewImg/dd9fdc8a9bce2e196281051bc1891360.jpg');
INSERT INTO `view` VALUES (76929, '马尔代夫奥静岛', '北海', '晴', '奥静岛位于马尔代夫马累环礁的西北部的圣吉利海滩，由著名的印度洋酒店品牌艾茉菲尔酒店集团（ Atmosphere Hotel &Resorts）经营管理，同时也是奥露岛（OBLU by Atmosphere）的姐妹酒店。奥静岛度假酒店为来马尔代夫的旅客提供了一个包罗万象的轻松的海滩度假体验。酒店内拥有各种“当代设计感”的独立别墅和套房，精选的餐厅和服务设施，是马尔代夫北马累环礁最美丽的岛屿之一。推荐理由：著名的印度洋酒店品牌艾茉菲尔酒店集团经营管理，宽敞的独立海滩和水上别墅 -（有些别墅含有附带私人泳池），升级的 THE SERENITY 一价全包计划，包括两个特色餐厅的精致餐饮体验 - JUST GRILL (特色烧烤餐厅)＆SIMPLYVEG（素食餐厅），以及别墅内可以免费使用的迷你吧！产品亮点：艾茉菲尔酒店集团旗下豪华五星岛屿，一价全包，省心，性价比高，2018年07月正式开业，设施新，开业促销给力。和姐妹岛奥露快艇衔接仅需20分钟，适合双岛游，岛上提供儿童俱乐部，设有专门的蜜月水上套房，适合家庭亲子游和蜜月游度假', '/asserts/img/viewImg/c3b995f1c15ec9c016fcdca56cffb147.jpg');
INSERT INTO `view` VALUES (77107, '秦始皇兵马俑', '西安', '大雨', '秦始皇兵马俑被称为世界第一大奇迹，规模巨大，气质恢宏，备受中外游客的关注，到这里游玩的时候可以找一个讲解员，一边游览一边了解历史背景和背后的故事能给人留下更深的记忆。\r\n\r\n　　门票：旺季(3月1日~11月31日)：150.00元 淡季(12月1日~2月28日)：120.00元\r\n\r\n　　开放时间：3月16日~11月14日： 8:30~17:30 11月15日~3月15日： 8:30~17:00\r\n\r\n　　地址：西安市临潼区临蓝路', '/asserts/img/viewImg/small3bb6c645c7922a9027dac1e08fd3777e1567645553.jpg');
